import './App.css';
import { GoogleLogin } from '@react-oauth/google';
import React, { useState, useEffect } from 'react';
import jwtDecode from 'jwt-decode';
import Cookies from 'js-cookie'; // Import js-cookie

function Navbar() {
  const [user, setUser] = useState();

  const responseGoogle = (response) => {
    setUser(response.credential);

    const decoded = jwtDecode(response.credential);

    try {
      if (decoded.email_verified) {
        // Set the credentials cookie with a 1-hour expiration time
        Cookies.set('credentials', response.credential, { expires: 1 / 24 }); // 1/24 is 1 hour

        // Handle user authentication as needed

      } else {
        console.error('Invalid response from Google Sign-In');
      }
    } catch (error) {
      console.error('Error during Google Sign-In:', error);
    }
  };

  return (
    <div style={{ background: "#419197", padding: "5px", textAlign: "center" }}>
      <p style={{ color: "white" }}>Navbar</p>
      {user ? (
        <p>User is authenticated</p>
      ) : (
        <GoogleLogin
          onSuccess={responseGoogle}
          onError={responseGoogle}
          cookiePolicy={'single_host_origin'}
          useOneTap
        />
      )}
    </div>
  );
}

export default Navbar;
