import React, { useState, useEffect } from "react";
import { Input } from 'reactstrap';
import { Button, Form, Row, Col } from 'reactstrap';


// https://dev.to/salehmubashar/search-bar-in-react-js-545l
// Object Destructing
function Search({fetchData}){

  
  const [student_ID, SetStudentDetails] = useState();

  function onChangeStudentDetails(e) {
    SetStudentDetails(e.target.value);
  }

  const handleSearch=()=>{
    
    fetchData(student_ID)
  }
    return (
        <div>
          <Form>
            <Row>
              <Col>
                <Input
                  id="Txt"
                  name="text"
                  placeholder="Enter ID Number"
                  type="text"
                  value = {student_ID}
                  onChange={onChangeStudentDetails}
                />
              </Col>
              <Col>
                <Button style={{backgroundColor : "#419197"}} onClick={handleSearch}>
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-search" viewBox="0 0 16 16">
                <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
                </svg>
                </Button>
              </Col>
            </Row>
          </Form>         
        </div>
      );
}

export default Search;
