import React from 'react';
import { GoogleLogin } from 'react-google-login';

class GoogleAuth extends React.Component {
  // This method is called when the user successfully logs in with Google
  responseGoogle = (response) => {
    if (response.profileObj) {
      const userData = {
        name: response.profileObj.name,
        email: response.profileObj.email,
        // You can include additional user data as needed
      };
      // Handle the user data, e.g., send it to your server or store it in your app's state
      console.log(userData);
    } else {
      // Handle the error or unsuccessful login
      console.error('Google login failed');
    }
  }

  render() {
    return (
      <div>
        <h2>Google Authentication</h2>
        <GoogleLogin
          clientId="YOUR_GOOGLE_CLIENT_ID"
          buttonText="Login with Google"
          onSuccess={this.responseGoogle}
          onFailure={this.responseGoogle}
          cookiePolicy={'single_host_origin'}
        />
      </div>
    );
  }
}

export default GoogleAuth;
