import { GoogleLogin } from '@react-oauth/google';
import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AdminContext } from '../App';
import jwtDecode from 'jwt-decode';
import Cookies from 'js-cookie'; // Import js-cookie

function Navbar() {
  const { setIsUserLoggedIn } = useContext(AdminContext);

  const [user, setUser] = useState();
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the user is authenticated when the component loads
    const storedCredentials = Cookies.get('credentials');
    if (storedCredentials) {
      setIsUserLoggedIn(true);
      setUser(storedCredentials);
    }
  }, [setIsUserLoggedIn]);

  const responseGoogle = (response) => {
    setUser(response.credential);

    const decoded = jwtDecode(response.credential);
    try {
      if (decoded.email_verified) {
        setIsUserLoggedIn(true);
        // const credentials = response.credential;
        navigate('/update');

        // Store the credentials in a cookie for persistence
        Cookies.set('credentials', user, { expires: 7 }); // Expires in 7 days
      } else {
        console.error('Invalid response from Google Sign-In');
      }
    } catch (error) {
      console.error('Error during Google Sign-In:', error);
    }
  };

  return (
    <div style={{ background: "#419197", padding: "5px", textAlign: "center" }}>
      <p style={{ color: "white" }}>Navbar</p>
      {/* {user ? (
        <p>User is authenticated</p>
      ) : ( */}
        <GoogleLogin
          onSuccess={responseGoogle}
          onError={responseGoogle}
          cookiePolicy={'single_host_origin'}
          useOneTap
        />
      //)}
    </div>
  );
}

export default Navbar;
