import './App.css';
import Search from './Components/Search';
import Navbar from './Components/Navbar';
import Cards from './Components/Card';
import React, { useState, useEffect } from 'react';
import axios from "axios";

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [student_ID, setStudentID] = useState('');
  
  const fetchData = async (student_ID) => {
    setLoading(true);
    try {
      console.log(student_ID)
      const response = await axios.get('http://localhost:8000/');
      const result = response.data
      setData(result);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
      // Set dataFetched to true to indicate that data has been fetched
   
    }
  };

 
  useEffect(() => {
    
    fetchData('');
  }, []);

  const handleSearch = (student_ID) => {

    setStudentID(student_ID);
    fetchData(student_ID);
  };

  return (
    <div style={{ padding: "10px" }}>
      <Navbar />
      <br />
      {/* Pass the handleSearch callback to the Search component */}
      <Search onSearch={handleSearch} student_ID={student_ID} />
      {loading ? (
        <p>Loading...</p>
      ) : (
       
         <Cards data={data} />
      )}
    </div>
  );
}

export default App;
