import React, { useState } from "react";
import { Input } from 'reactstrap';
import { Button, Form, Row, Col } from 'reactstrap';
import Card from "../Components/Card"
import UserCard from "../Add_User/Components/Update";
function Search() {

  const [student_ID, setStudentID] = useState()
  
  const onChangestudentID = (e)=>{
    setStudentID(e.target.value)
  }

  const [studentDetails, setStudentDetails] = useState()

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    try {
      const url = 'http://localhost:8000/';
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ student_ID }),
      });
  
      if (response.ok) {
        const data = await response.json();
        
        setStudentDetails(data);
   
      } else {
        throw new Error('Request failed');
      }
    } catch (error) {
      console.log(error);
    }
  };
  
  const handleEditUser = (updatedUser) => {
    setStudentDetails(updatedUser);
  };
  return (
    <div>
     
      <br />
      <Form>
        <Row>
          <Col>
            <Input
              id="Txt"
              name="text"
              placeholder="Enter ID Number"
              type="text"
              value={student_ID}
              onChange={onChangestudentID}
            />
          </Col>
          <Col>
            <Button style={{ backgroundColor: "#419197" }} type="submit" onClick={handleSubmit}>
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-search" viewBox="0 0 16 16">
                <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
              </svg>
            </Button>
          </Col>
        </Row>
      </Form>
      {studentDetails && <Card data={studentDetails} />}
      {/* {studentDetails && <UserCard data={studentDetails} />} */}
      {studentDetails && <UserCard studentDetails={studentDetails} onEdit={handleEditUser}   />}
    </div>
  );
}

export default Search;
